import {Component, OnInit} from '@angular/core';
import {ModalDismissReasons, NgbModal} from '@ng-bootstrap/ng-bootstrap';
import {Stock} from '../shared/Model/Stock';
import {StockService} from '../shared/Service/Stock.service';

@Component({
  selector: 'app-stock',
  templateUrl: './stock.component.html',
  styleUrls: ['./stock.component.css']
})
export class StockComponent implements OnInit {

  listStocks: any;
  form: boolean = false;
  stock!: Stock;
  closeResult!: string;

  constructor(private stockService: StockService, private modalService: NgbModal) {
  }

  ngOnInit(): void {
    this.getAllStockss();

    this.stock = {
      idStock: null,
      libelleStock:null,
      qte:null,
      qteMin:null
    }
  }

  getAllStockss() {
    this.stockService.getAllStocks().subscribe(res => this.listStocks = res)
  }

  addStock(p: any) {
    this.stockService.addStock(p).subscribe(() => {
      this.getAllStockss();
      this.form = false;
    });
  }

  editStock(stock: Stock) {
    this.stockService.editStock(stock).subscribe();
  }

  deleteStock(idStock: any) {
    this.stockService.deleteStock(idStock).subscribe(() => this.getAllStockss())
  }

  open(content: any, action: any) {
    if (action != null)
      this.stock = action
    else
      this.stock = new Stock();
    this.modalService.open(content, {ariaLabelledBy: 'modal-basic-title'}).result.then((result) => {
      this.closeResult = `Closed with: ${result}`;
    }, (reason) => {
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });
  }

  private getDismissReason(reason: any): string {
    if (reason === ModalDismissReasons.ESC) {
      return 'by pressing ESC';
    } else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
      return 'by clicking on a backdrop';
    } else {
      return `with: ${reason}`;
    }
  }

  cancel() {
    this.form = false;
  }
}
<div class="row">
  <div class="col-md-11" style="margin-left: 20px">
    <h3 style="margin-top: 20px">List Stock </h3>
    <hr>
    <button class="btn btn-round btn-sm btn-success" (click)="open(mymodal1,null)">Add new Stock</button>
    <br><br>
    <table class="table table-hover">
      <thead>
      <tr>
        <th>Id</th>
        <th>Libelle</th>
        <th>Quantité</th>
        <th>Quantité Min</th>
        <th>Action</th>
      </tr>
      </thead>
      <tbody>
      <tr *ngFor='let p of listStocks'>
        <td>{{p.idStock}}</td>
        <td>{{p.libelleStock}}</td>
        <td>{{p.qte}}</td>
        <td>{{p.qteMin}}</td>
        <td>
          <button type="button" class="btn btn-outline-primary"
                  (click)="open(mymodal1,p)">Edit
          </button>
          &nbsp;
          <button type="button" class="btn btn-outline-danger"
                  (click)="deleteStock(p.idStock)">Delete
          </button>
        </td>
      </tr>
      </tbody>
    </table>

  </div>
</div>
<ng-template #mymodal1 let-modal1>
  <div class="modal-header">
    <h4 class="modal-title" id="modal-basic-title">Add new Stock</h4>
    <button type="button" class="btn-close" aria-label="Close" (click)="modal1.dismiss('Cross click')"></button>
  </div>
  <div class="modal-body">
    <form>
      <div class="mb-3">
        <label for="lib">Stock Libelle</label>
        <div class="input-group">
          <input id="lib" class="form-control" type="text" placeholder="Stock Libelle"
                 [(ngModel)]="stock.libelleStock"
                 [ngModelOptions]="{standalone : true}"></div>
      </div>
      <div class="mb-3">
        <label for="qte">Stock Qte</label>
        <div class="input-group">
          <input id="qte" class="form-control" type="number" placeholder="Stock Qte"
                 [(ngModel)]="stock.qte"
                 [ngModelOptions]="{standalone : true}"></div>
      </div>
      <div class="mb-3">
        <label for="qteM">Stock Qte</label>
        <div class="input-group">
          <input id="qteM" class="form-control" type="number" placeholder="Stock Qte Min "
                 [(ngModel)]="stock.qteMin"
                 [ngModelOptions]="{standalone : true}"></div>
      </div>
    </form>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-success" (click)="addStock(stock)"
            (click)="modal1.close('Save click')">Add
    </button>
    <button type="button" class="btn btn-danger" (click)="modal1.close('Save click')">Cancel</button>
  </div>
</ng-template>
