import {Component, OnInit} from '@angular/core';
import {ModalDismissReasons, NgbModal} from '@ng-bootstrap/ng-bootstrap';
import {SecteurActivite} from '../shared/Model/Secteur-activite';
import {SecteurActiviteService} from '../shared/Service/Secteur-activite.service';

@Component({
  selector: 'app-secteur-activite',
  templateUrl: './secteur-activite.component.html',
  styleUrls: ['./secteur-activite.component.css']
})
export class SecteurActiviteComponent implements OnInit {

  listSec: any;
  form: boolean = false;
  sec!: SecteurActivite;
  closeResult!: string;

  constructor(private secteurActiviteService: SecteurActiviteService, private modalService: NgbModal) {
  }

  ngOnInit(): void {
    this.getAllSec();
    this.sec = {
      idSecteurActivite:null,
      codeSecteurActivite:null,
      libelleSecteurActivite:null
    }
  }

  getAllSec() {
    this.secteurActiviteService.getAllSecteurActivites().subscribe(res => this.listSec = res)
  }

  addSec(p: any) {
    this.secteurActiviteService.addSecteurActivite(p).subscribe(() => {
      this.getAllSec();
      this.form = false;
    });
  }

  editSec(sec: SecteurActivite) {
    this.secteurActiviteService.editSecteurActivite(sec).subscribe();
  }

  deleteSec(idSec: any) {
    this.secteurActiviteService.deleteSecteurActivite(idSec).subscribe(() => this.getAllSec())
  }

  open(content: any, action: any) {
    if (action != null)
      this.sec = action
    else
      this.sec = new SecteurActivite();
    this.modalService.open(content, {ariaLabelledBy: 'modal-basic-title'}).result.then((result) => {
      this.closeResult = `Closed with: ${result}`;
    }, (reason) => {
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });
  }

  private getDismissReason(reason: any): string {
    if (reason === ModalDismissReasons.ESC) {
      return 'by pressing ESC';
    } else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
      return 'by clicking on a backdrop';
    } else {
      return `with: ${reason}`;
    }
  }

  cancel() {
    this.form = false;
  }
}
<div class="row">
  <div class="col-md-11" style="margin-left: 20px">
    <h3 style="margin-top: 20px">List Secteur-Activite </h3>
    <hr>
    <button class="btn btn-round btn-sm btn-success" (click)="open(mymodal1,null)">Add new Secteur-Activite</button>
    <br><br>
    <table class="table table-hover">
      <thead>
      <tr>
        <th>Id</th>
        <th>Code</th>
        <th>Libelle</th>
        <th>Action</th>
      </tr>
      </thead>
      <tbody>
      <tr *ngFor='let p of listSec'>
        <td>{{p.idSecteurActivite}}</td>
        <td>{{p.codeSecteurActivite}}</td>
        <td>{{p.libelleSecteurActivite}}</td>
        <td>
          <button type="button" class="btn btn-outline-primary"
                  (click)="open(mymodal1,p)">Edit
          </button>
          &nbsp;
          <button type="button" class="btn btn-outline-danger"
                  (click)="deleteSec(p.idSecteurActivite)">Delete
          </button>
        </td>
      </tr>
      </tbody>
    </table>

  </div>
</div>
<ng-template #mymodal1 let-modal1>
  <div class="modal-header">
    <h4 class="modal-title" id="modal-basic-title">Add new Secteur-Activite</h4>
    <button type="button" class="btn-close" aria-label="Close" (click)="modal1.dismiss('Cross click')"></button>
  </div>
  <div class="modal-body">
    <form>
      <div class="mb-3">
        <label for="sacode">Secteur-Activite Code</label>
        <div class="input-group">
          <input id="sacode" class="form-control" type="text" placeholder="Product Code"
                 [(ngModel)]="sec.codeSecteurActivite"
                 [ngModelOptions]="{standalone : true}"></div>
      </div>
      <div class="mb-3">
        <label for="salibelle">Secteur-Activite libelle</label>
        <div class="input-group">
          <input id="salibelle" class="form-control" type="text" placeholder="Product Name"
                 [(ngModel)]="sec.libelleSecteurActivite"
                 [ngModelOptions]="{standalone : true}"></div>
      </div>
    </form>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-success" (click)="addSec(sec)"
            (click)="modal1.close('Save click')">Add
    </button>
    <button type="button" class="btn btn-danger" (click)="modal1.close('Save click')">Cancel</button>
  </div>
</ng-template>
